const dummyUser = {
    id : 1,
    Posts : [{id:'dummyUserID'},{id:'lll'}],
    Followings : [{id:'h'}],
    Followers : [{id:'k'},{id:'h'}],
}

export const initialState = {
    isLoggedIn : false,
    me : null,
    signUpDate : {},
    loginData : [],
}

export const loginAction = (data) => {
    return {
        type : 'LOG_IN',
        data,
    }
}
export const logoutAction = (data) => {
    return {
        type : 'LOG_OUT',
        data,
    }
}
export const Follow = () =>{
    console.log('FollowAction');
    return{
        type : 'FOLLOW',
    }
}
export const unFollow = (data)=>{
    return {
        type : 'UNFOLLOW',
        data,
    }
}
const dummyFollower = {
    id : 'halomalo',
}

const reducer = (state=initialState, action)=>{
    switch(action.type){
        case 'LOG_IN':
            return{
                ...state,
                isLoggedIn : true,
                me : dummyUser,
            };
        case 'LOG_OUT':
            return{
                ...state,
                isLoggedIn : false,
                me : null,
            };
        case 'FOLLOW':
            return{
                ...state,
                me: {
                    ...state,
                    Followings: [dummyFollower, ...state.me.Followings],
                }
            };
        default : 
            return state;
    }
};

export default reducer;
