const dummyUser = {
    id : 1,
    Posts : [{id:'dummyUserID'},{id:'lll'}],
    Followings : [{id:'h'}],
    Followers : [{id:'k'},{id:'h'}],
}
export const initialState = {
    isLoggingIn : false, 
    isLoggedIn : false,
    isLoggingOut : false,
    me : null,
    signUpDate : {},
    loginData : [],
}

export const loginAction = (data)=>{
    return (dispatch, getState)=>{
        const state = getState();
        dispatch(loginRequestAction());
        axios.post('/api/login')
            .then((res)=>{
                dispatch(loginSuccessAction(res.data));
            })
            .catch((err)=>{
                dispatch(loginFailureAction(err));
            })
    }    
}
export const loginRequestAction = (data) => {
    return {
        type : 'LOG_IN_REQUEST',
        data,
    }
}
export const logoutRequestAction = (data) => {
    return {
        type : 'LOG_OUT_REQUEST',
        data,
    }
}
export const loginSuccessAction = (data) => {
    return {
        type : 'LOG_IN_SUCCESS',
        data,
    }
}
export const logoutSuccessAction = (data) => {
    return {
        type : 'LOG_OUT_SUCCESS',
        data,
    }
}
export const loginFailureAction = (data) => {
    return {
        type : 'LOG_IN_FAILURE',
        data,
    }
}
export const logoutFailureAction = (data) => {
    return {
        type : 'LOG_OUT_FAILURE',
        data,
    }
}
export const Follow = () =>{
    console.log('FollowAction');
    
    return{
        type : 'FOLLOW',
    }
}
export const unFollow = (data)=>{
    return {
        type : 'UNFOLLOW',
        data,
    }
}
const dummyFollower = {
    id : 'halomalo',
}

const reducer = (state=initialState, action)=>{
    switch(action.type){
        case 'LOG_IN_REQUEST':
            return{
                ...state,
                isLoggingIn : true,
                isLoggedIn : true,
                me : action.data,
            };
        case 'LOG_IN_SUCCESS':
                return{
                    ...state,
                    isLoggingIn : false,
                    isLoggedIn : true,
                    me : action.data,
                };
        case 'LOG_IN_FAILURE':
                    return{
                        ...state,
                        isLoggingIn : false,
                        isLoggedIn : false,
                    };
        case 'LOG_OUT_REQUEST':
            return{
                ...state,
                isLoggingOut : true,
            };
        case 'LOG_OUT_SUCCESS':
                return{
                    ...state,
                    isLoggedIn : false,
                    isLoggingOut : false,
                    me : null,
                };
        case 'LOG_OUT_FAILURE':
            return{
                ...state,
                isLoggingOut : false,
            };
        case 'FOLLOW':
            return{
                ...state,
                me: {
                    ...state.me,
                    Followings: [dummyFollower, ...state.me.Followings],
                }
            };
        case 'UNFOLLOW':
            return{
                ...state,
                me: {
                    ...state.me,
                    Followings:[{id:'h'}],
                }
            };
        default : 
            return state;
    }
};

export default reducer;
