import React, {useState} from 'react';
import PropTypes from 'prop-types';
import Slick from 'react-slick';
import styled ,{createGlobalStyle} from 'styled-components';

const Overlay=styled.div   `
    position : fixed;
    z-index:5000;
    top:0;
    left:0;
    right :0;
    bottom:0;
`;
//함수 호출, () 와 `` 가 같다. func`` (javascript 문법)


const Header = styled.header`
    height:44px;
    backgroudn:white;
    position:relative;
    padding:0;
    text-align:center;
    & h1{
        margin:0;
        font-size:17px;
        color:#333;
        line-height:44px;
    }

    & button{
        position : absoulte;
        right:0;
        top:0;
        padding:15px;
        line-height:14px;
        cursor:pointer;
    }
`;

const SlickWrapper=styled.div`
    height:calc(100% - 44px);
    background:#090909;
`;

const ImgWrapper = styled.div`
    padding:32px;
    text-align:center;
    & img{
        margin:0 auto;
        max-height:750px;
    }
`

const Indicator = styled.div`
  text-align: center;
  
  & > div {
    width: 75px;
    height: 30px;
    line-height: 30px;
    border-radius: 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color: white;
    font-size: 15px;
  }
`;
const Global = createGlobalStyle`
  .slick-slide{
      display:inline-block;

  }
`
// Slick 각 클래스명이 다 있는데 그걸 다 적어줄 수 없기떄문에 덮어씌어 주는 것


const ImagesZoom = ({ images, onClose}) => {
    const [currentSlide, setCurrentSlide] = useState(0);
    //현재 슬라이드를 state로 저장해두기 처음은 0
    return(
        <Overlay>
            <Global/>
            <Header>
                <h1>상세이미지</h1>
                <button onClick={onClose}>X</button>
            </Header>
            <SlickWrapper>
            <div>
                <div>
                    <Slick
                        initialSlid={0}
                        afterChange={(slide) => setCurrentSlide(slide)}
                        infinite //무한반복
                        arrows={false}
                        slideToShow={1} //한번에 하나만 보여주고, 한번에 하나만 스크롤
                        slideToScroll={1}
                    >
                        {/*이미지 한장 한장 넣어주기, 슬릭이 div안에 있는 것을 자동으로 캐루세롤 만들어준다*/}
                        {images.map((x)=>{
                            <ImgWrapper key={x.src}>
                                <img src={x.src} alt={x.src} />
                            </ImgWrapper>
                        })}
                    </Slick>
                </div>
            </div>
            </SlickWrapper>
        </Overlay>
    )

}
ImagesZoom.propTypes={
    images : PropTypes.arrayOf(PropTypes.object).isRequired,
    onClose : PropTypes.func.isRequired,
}
export default ImagesZoom;
