import React from 'react';
import PropTypes from 'prop-types';
import {useSelector} from 'react-redux'
import {Card, Popover, Button} from 'antd';
import {RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined} from '@ant-design/icons';
import PostImages from './PostImages';



const PostCard = ({post}) => {
    const {me}= useSelector((state)=>state.user);
    const id = me?.id;
    // === me && me.id
    return (
        <div>
            
            {/* 배열안에 jsx를 넣을때는 항상 key 붙여주기
                    Popover는 ...에 손을 올렸을 때 여러가지 더보기 버튼들 */}
            <Card
                cover={post.Images[0] && <PostImages images={post.Images}/>}
                actions = {[
                    <RetweetOutlined key="retweet"/>,
                    <HeartOutlined key="heart"/>,
                    <MessageOutlined key="comment"/>,
                    <Popover key="more" content={(
                        <Button.Group>
                            {id && post.User.id === id ? (
                                <>
                                <Button>수정</Button>
                                <Button type="danger">삭제</Button>
                                </>
                            ) : <Button>신고</Button>}

                        </Button.Group>
                    )}>
                        <EllipsisOutlined/>
                    </Popover>
                ]}
            >
                <Card.Meta
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    title={post.User.nickname}
                    description={<PostCardContent postData={post.content} />}
                />
                </Card>
                {/*
            <CommentForm/>
            <Coments/>
                */}
        </div>
    )
}
PostCard.propTypes = {
    post : PropTypes.shape({
        id : PropTypes.number,
        User : PropTypes.object,
        content : PropTypes.string,
        createAt : PropTypes.object,
        Comments : PropTypes.arrayOf(PropTypes.object),
        Images : PropTypes.arrayOf(PropTypes.object),
    }).isRequired,
};
{/*shape으로 더 구체적으로 적어줄 수 있다. 처음에
post : PropTypes.object.isRequired 로 간단하게 적어줄 수 있음*/}
export default PostCard;
