export const initialState = {
    isLoggedIn : false,
    me : null,
    signUpDate : {},
    loginData : [],
    Posts : [],
    Followings : [],
    Followers : [],
}

export const loginAction = (data) => {
    return {
        type : 'LOG_IN',
        data,
    }
}
export const logoutAction = (data) => {
    return {
        type : 'LOG_OUT',
        data,
    }
}
export const Follow = (data) =>{
    return {
        type : 'FOLLOW',
        data,
    }
}
export const unFollow = (data)=>{
    return {
        type : 'UNFOLLOW',
        data,
    }
}
const dummyFollower = {
    id : 'halomalo',
}

const reducer = (state=initialState, action)=>{
    switch(action.type){
        case 'LOG_IN':
            return{
                ...state,
                isLoggedIn : true,
                    me : action.data,
            };
        case 'LOG_OUT':
            return{
                ...state,
                isLoggedIn : false,
                me : null,
            };
        case 'FOLLOW':
            return{
                ...state,
                followings : [dummyFollower, ...state],
            }
        default : 
            return state;
    }
};

export default reducer;
