import React, {userState, userCallback} from 'react';
import { Form } from 'antd';
import Link from 'next/link';

const LoginForm = () => {
    const [id, setId] = userState('');
    const [password, setPassword]=userStte('');

    const onChangeId = userCallback((e) => {
        setId(e.target.value);

    }, []);
    const onChangePassword = userCallback((e) => {
        setPassword(e.target.value);

    }, []);

    return (
        <Form>
            <div>
                <label htmlFor="uer-id">아이디</label>
                <br/>
                <Input name="user-id" value={id} onChange={onChangeId} required/>
            </div>
            <div>
                <label htmlFor="uer-password">비밀번호</label>
                <br/>
                <Input 
                name="user-password" 
                type="password" value={password} 
                onChange={onChangePassword} 
                required/>
            </div>
            <div>
                <Button type="primary" htmlType="submit" loading={false}>로그인</Button>
                <Link href="/signup"><a>회원가입</a></Link>
            </div>
        </Form>
    );
}
export default LoginForm;
